	                const nearFactor = (50 - p.clipNear) / 50;
	                const farFactor = -(50 - p.clipFar) / 50;
	                this.camera.near = this.cDist - (this.bRadius * nearFactor);
	                this.camera.far = this.cDist + (this.bRadius * farFactor);
