	function isMasterContact(ap1, ap2, masterIdx) {
	    return ((ap1.modelIndex === masterIdx && ap2.modelIndex !== masterIdx) ||
	        (ap2.modelIndex === masterIdx && ap1.modelIndex !== masterIdx));
	}
